import {useState} from 'react'

function Login () {

    const {email, setEmail} = useState("")
    const {password, setPassword} = useState("")

    const handleLogin = (e) => {
        e.preventDefault()
        setEmail(e.target.value)
        window.alert(`Email: ${email}, Password: ${password}`)
    }

    return (
     <div>
     <div className="login-form-wrap">
           <h2>Login</h2>
           <form className="login-form">
             <input type="email" value={email}  name="email" placeholder="email" required/>
             <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} name="password" placeholder="password" required/>
             <button onClick={(e) => handleLogin(e)} type="submit" className="btn-login">Login</button>
           </form>
         </div>
   </div>
    )
   }

   export default Login
